INSERT INTO muzej (adresa, koordinate, naziv, kratak_opis, maps_link, txt0, txt1, txt2) VALUES ('Rue de Rivoli, Paris', '48.8606, 2.3376', 'The Louvre', 'Najveći muzej na svetu', 'maps.google.com/Louvre', 'Muzej Luvr u Parizu je jedan od najpoznatijih i najvećih muzeja umetnosti na svetu. Smešten u istorijskoj palati, domaćin je bezbrojnim umetničkim delima, uključujući Mona Lizu i Veneru iz Milosa. Luvr je prepoznatljiv po svojoj staklenoj piramidi i bogatoj istoriji.', 'Muzej Luvr, smešten u srcu Pariza, je jedan od najznačajnijih muzeja umetnosti u svetu. Otvoren 1793. godine, prvobitno je bio kraljevska palata, a danas se ističe kao dom mnogim svetski poznatim umetničkim delima. Kolekcija uključuje Mona Lizu, Veneru iz Milosa, kao i brojne druge umetničke dragocenosti iz različitih perioda i kultura. Luvr je poznat po svojoj impozantnoj staklenoj piramidi koja služi kao glavni ulaz i simbol muzeja. Uz bogatu umetničku kolekciju, Luvr nudi i fascinantnu arhitekturu i istoriju, čineći ga nezaobilaznim odredištem za ljubitelje umetnosti.', 'Muzej Luvr u Parizu je jedan od najpoznatijih muzeja umetnosti na svetu, s bogatom istorijom i impresivnom kolekcijom. Otvoren 1793. godine, Luvr je prvobitno bio kraljevska palata pre nego što je pretvoren u muzej. Smešten u impresivnoj zgradi, Luvr je domaćin mnogim neprocenjivim umetničkim delima, uključujući Mona Lizu, Veneru iz Milosa, i mnoge druge dragocenosti iz različitih istorijskih perioda i kultura. Muzej je prepoznatljiv po svojoj staklenoj piramidi, koja je postavljena kao glavni ulaz 1989. godine i postala simbol muzeja. Luvr je ne samo kulturno središte sa bogatom kolekcijom, već i arhitektonski dragulj koji odražava svoju dugu i značajnu istoriju. Poseta ovom muzeju pruža jedinstveno iskustvo umetnosti i kulture.');
INSERT INTO muzej (adresa, koordinate, naziv, kratak_opis, maps_link, txt0, txt1, txt2) VALUES ('Trafalgar Square, London', '51.5086, -0.1283', 'National Gallery', 'Nacionalna galerija', 'maps.google.com/NG', 'Nacionalna galerija u Londonu, smeštena na Trafalgar Square-u, dom je izvanredne kolekcije evropske umetnosti. Otvorena 1824. godine, nudi dela velikih majstora kao što su Van Gogh, Leonardo da Vinci i Rembrandt. Ulaz je besplatan, što je čini pristupačnom za sve ljubitelje umetnosti.', 'Nacionalna galerija u Londonu, smeštena na Trafalgar Square-u, jedno je od najvažnijih umetničkih institucija u Velikoj Britaniji. Osnovana 1824. godine, galerija sadrži impresivnu kolekciju evropske umetnosti koja obuhvata radove od srednjeg veka do 1900. godine. Kolekcija uključuje dela velikih umetnika kao što su Leonardo da Vinci, Vincent van Gogh, Rembrandt, i mnogi drugi. Muzej je poznat po svojoj besplatnoj ulaznici, što ga čini dostupnim široj publici. Nacionalna galerija ne samo da nudi umetnički užitak, već i predstavlja značajan deo kulturnog nasleđa Londona.', 'Nacionalna galerija u Londonu, smeštena na Trafalgar Square-u, jedno je od najvažnijih umetničkih muzeja u Velikoj Britaniji i svetu. Otvorena 1824. godine, galerija je dom za jednu od najznačajnijih kolekcija evropske umetnosti koja obuhvata period od srednjeg veka do početka 20. veka. Među njenim eksponatima nalaze se dela velikih umetnika poput Leonarda da Vincija, Vincenta van Gogha, Rembrandta, i mnogih drugih. Pored svoje izvanredne umetničke kolekcije, Nacionalna galerija je poznata po tome što nudi besplatan ulaz za sve posetioce, čineći umetnost dostupnom široj javnosti. Sa bogatom zbirkom koja uključuje slike, skulpture i crteže, galerija pruža duboko kulturno iskustvo i ključni je deo londonske umetničke i kulturne scene.');
INSERT INTO muzej (adresa, koordinate, naziv, kratak_opis, maps_link, txt0, txt1, txt2) VALUES ('1071 5th Ave, New York', '40.7829, -73.9654', 'Guggenheim', 'Muzej moderne umetnosti', 'maps.google.com/Guggenheim', 'Muzej Guggenheim u New Yorku, otvoren 1959. godine, poznat je po svojoj jedinstvenoj spirali arhitekture koju je dizajnirao Frank Lloyd Wright. Muzej je domaćin savremene umetnosti i nudi kolekcije koje obuhvataju radove iz 20. i 21. veka. Njegova prepoznatljiva zgrada i umetnička zbirka čine ga posebnim.', 'Muzej Guggenheim u New Yorku, otvoren 1959. godine, jedno je od najprepoznatljivijih umetničkih odredišta u svetu. Dizajnirao ga je Frank Lloyd Wright i poznat je po svojoj inovativnoj spirali koja se uzdiže od prizemlja do krova. Muzej je posvećen modernoj i savremenoj umetnosti, sa kolekcijama koje obuhvataju radove iz 20. i 21. veka. Osim svoje jedinstvene arhitekture, Guggenheim nudi izložbe i zbirke koje uključuju dela najuticajnijih umetnika tog perioda. Zgrada sama po sebi je umetničko delo i predstavlja značajan doprinos modernoj arhitekturi.', 'Muzej Guggenheim u New Yorku, otvoren 1959. godine, predstavlja vrhunski primer moderne arhitekture i savremene umetnosti. Dizajnirao ga je Frank Lloyd Wright, a njegova zgrada je prepoznatljiva po svojoj spiralnoj formi koja se uzdiže od prizemlja do krova, pružajući jedinstvenu vizualnu i prostornu perspektivu. Muzej je posvećen prikupljanju i izlaganju modernih i savremenih umetničkih dela, sa kolekcijama koje obuhvataju radove iz 20. i 21. veka. Guggenheim je poznat po svojoj inovativnoj arhitekturi, koja sama po sebi predstavlja umetničko delo, kao i po značajnim izložbama koje uključuju radove najuticajnijih umetnika tog perioda. Sa svojom jedinstvenom formom i bogatom umetničkom ponudom, Guggenheim je značajan deo kulturnog pejzaža New Yorka i globalne umetničke scene.');
INSERT INTO muzej (adresa, koordinate, naziv, kratak_opis, maps_link, txt0, txt1, txt2) VALUES ('Av. Winston Churchill, Paris', '48.8662, 2.3136', 'Grand Palais', 'Poznata galerija', 'maps.google.com/GP', 'Grand Palais u Parizu je impozantna zgrada izgrađena 1900. godine, poznata po svojoj veličanstvenoj arhitekturi i staklenom krovu. Koristi se za razne umetničke i kulturne događaje, uključujući izložbe, sajmove i koncerte. Njegova grandiozna građevina čini ga ključnim delom pariskog kulturnog pejzaža.', 'Grand Palais u Parizu, izgrađen za Svetsku izložbu 1900. godine, je veličanstvena zgrada sa impozantnom arhitekturom i velikim staklenim krovom. Smesten na aveniji Champs-Élysées, Grand Palais je domaćin raznih kulturnih i umetničkih događaja, uključujući velike izložbe, sajmove, koncerte i modne revije. Zgrada je prepoznatljiva po svom neoklasičnom stilu, bogatim detaljima i monumentalnoj fasadi. Pored toga, Grand Palais ima značajnu ulogu u pariskom kulturnom životu i predstavlja ključni deo francuske umetničke baštine.', 'Grand Palais u Parizu je impresivna zgrada koja je izgrađena za Svetsku izložbu 1900. godine. Nalazi se na prestižnoj aveniji Champs-Élysées i poznata je po svojoj monumentalnoj arhitekturi i velikom staklenom krovu koji omogućava obilje prirodne svetlosti. Dizajnirana u neoklasičnom stilu sa bogatim detaljima i elegantnim oblicima, zgrada je dom za razne umetničke i kulturne događaje, uključujući velike izložbe, sajmove, koncerte, i modne revije. Grand Palais ima značajnu ulogu u kulturnom životu Pariza i često se koristi za manifestacije od međunarodnog značaja. Sa svojom impozantnom fasadom i bogatom unutrašnjosti, predstavlja ključni deo francuske umetničke baštine i značajan deo pariskog kulturnog pejzaža.');
INSERT INTO muzej (adresa, koordinate, naziv, kratak_opis, maps_link, txt0, txt1, txt2) VALUES ('Piazza del Duomo, Florence', '43.7731, 11.2559', 'Uffizi Gallery', 'Istorijski muzej', 'maps.google.com/Uffizi', 'Uffizi Gallery u Firenci je jedan od najvažnijih umetničkih muzeja na svetu, otvoren 1584. godine. Poznat je po izvanrednoj kolekciji renesansne umetnosti, uključujući dela Botticellija, Da Vincija i Mikelanđela. Zgrada sama po sebi je arhitektonski dragulj sa pogledom na reku Arno.', 'Uffizi Gallery u Firenci, osnovan 1584. godine, je jedan od najvažnijih i najprestižnijih muzeja umetnosti na svetu. Nalazi se u istorijskoj zgradi dizajniranoj od strane arhitekte Giorgio Vasarija. Muzej je poznat po svojoj impresivnoj kolekciji renesansne umetnosti, uključujući remek-dela kao što su Botticellijeva "Rođenje Venere", Da Vincijev "Gospodin s prstenom" i Mikelanđelova dela. Uffizi Gallery je ključna destinacija za ljubitelje umetnosti i istoričare, a njegova bogata kolekcija i prelepa arhitektura čine ga jednim od najznačajnijih kulturnih središta u Italiji.', 'Uffizi Gallery u Firenci, osnovana 1584. godine, predstavlja jedan od najvažnijih muzeja umetnosti na svetu, sa bogatom kolekcijom renesansne umetnosti. Muzej je smešten u istorijskoj zgradi koju je dizajnirao Giorgio Vasari, a koja se izdiže uz reku Arno i nudi prelep pogled na grad. Uffizi je poznat po izuzetnoj zbirci umetničkih dela, uključujući Botticellijevu "Rođenje Venere", Da Vincijev "Gospodin s prstenom" i Mikelanđelovu "Sveta porodica". Zgrada muzeja sama po sebi je arhitektonski dragulj, a unutrašnjost sadrži bogate freske i dekoracije koje dopunjuju umetničku izložbu. Kao ključna destinacija za ljubitelje umetnosti i istoričare, Uffizi Gallery nudi nezaboravno iskustvo i duboko uvid u renesansnu umetnost i kulturu.');
